import React, { Component } from 'react';
import './main.css';

export default class Main extends Component {
  render() {
    return (
      <div className="container mx-auto p-40">
        <h1 className="text-6xl text-white font-bold leading-none">SCOTT<br/>CARDOZA</h1>
        <a className="text-white p-4 pl-1 text-2xl " rel="noopener noreferrer" href="https://github.com/scdozer/" target="_blank"><i className="fa fa-github" aria-hidden="true"></i></a>
        <a className="text-white p-4 text-2xl " rel="noopener noreferrer" href="https://www.linkedin.com/in/scott-cardoza-b5497974/" target="_blank"><i className="fa fa-linkedin-square" aria-hidden="true"></i></a>
        <a className="text-white p-4 text-2xl " rel="noopener noreferrer" href="mailto:scott@scottcardoza.com" target="_blank"><i className="fa fa-envelope-o" aria-hidden="true"></i></a>

        <p className="text-5xl text-gray-100 pt-20 font-bold">I make things for the web.</p>

        <p className="text-4xl text-gray-100 pt-20">Since Febuary 2015, I have been working at <a rel="noopener noreferrer" href="https://consumerfocusmarketing.com/" target="_blank">Consumer Focus Marketing</a> as a Web Developer. We work a lot with Wordpress.</p>

        <hr className="pt-2"/>

        <p className="text-4xl text-gray-100 pt-20">I watched a lot of videos and the ones I enjoyed can be found <a href="https://timewellwasted.online">here</a>. I made the site using Wordpress and React.</p>

        <p className="text-4xl text-gray-100 pt-20">I helped a friend <a href="https://droneopsco.com/" target="_blank" rel="noopener noreferrer">make this website</a> using Wordpress as a headless CMS using Gatsby and TailWindCSS.</p>

        <p className="text-4xl text-gray-100 pt-20"> </p>

        <p className="text-4xl text-gray-100 pt-20"> </p>

      </div>
    )
  }
}
